// 提示窗口
function Eject() {
    $("<style></style>").text('.qback{width:100%;height:100%;background:rgba(0,0,0,.3);position:fixed;top:0;left:0;z-index:9999999999999999999999}.alertBox{width:7.5rem;height:auto;overflow:hidden;box-sizing:border-box;background:#fff;border-radius:.3rem;position:relative;top:50%;left:50%;margin:0 0 0 -3.7rem;padding-bottom:.5rem;}.alertHead{width:100%;background-color:#00bcd5;border-bottom:1px solid #99bafa;font-size:.5rem;color:#fff;padding:.15rem 0;text-align:center}.alertMes{width:100%;height:auto;overflow:hidden;padding:1.25rem .5rem;text-align:center;font-size:.45rem;color:#333;box-sizing:border-box;}.alertBtn{display:block;width:100px;height:34px;line-height:34px;background-color:#00bcd5;cursor:pointer;font-size:14px;color:#fff;border-radius:4px;border:1px solid transparent;margin:0 auto;text-align:center}.ConBtn{padding:.15rem 0;display:inline-block;width:2.5rem;background-color:#00bcd5;cursor:pointer;font-size:.36rem;color:#fff;border-radius:4px;border:1px solid transparent;text-align:center;margin-left:.9rem;margin-right:.7rem;}.cancel{padding:.15rem 0;display:inline-block;width:2.5rem;background:fff;cursor:pointer;font-size:14px;color:#00bcd5;border-radius:4px;border:1px solid transparent;text-align:center;border:1px solid #00bcd5;}.ToastBox{width:4rem;height:auto;padding:.3rem 0;text-align:center;background:rgb(0,188,213);color:#fff;font-size:.36rem;border-radius:8px;position:relative;bottom:-70%;box-shadow:0 0 5px #d2cccc;left:50%;margin:0 0 0 -2rem}').appendTo($("head"));
    var _this = this;
    var offOff = true;
    var qback = $('<div class="qback"></div>')
    _this.Ealert = function (obj) {
        if (offOff === true) {
            var alertBox = $('<div class="alertBox"></div>')
            // var alertHead = $('<div class="alertHead">' + obj.title + '</div>')
                console.log( )
            obj.qrqrqr ===undefined? obj.qrqrqr='确认':obj.qrqrqr
            var alertMes = $('<div class="alertMes">' + obj.message + '</div>')
            var alertBtn = $('<span class="alertBtn">' + obj.qrqrqr + '</span>').on('click', function () {
                qback.remove();
                alertBox.remove();
                offOff= true;
                if(obj.hasOwnProperty('define')){
                    setTimeout(function () {
                        obj.define()
                    }, 100)
                }
            })
            // alertBox.append(alertHead);
            alertBox.append(alertMes);
            alertBox.append(alertBtn);
            qback.append(alertBox);
            $('body').append(qback);
            alertBox.css({
                'marginTop': -alertBox.outerHeight() / 2 + 'px'
            });
        }
    }
    _this.Econfirm = function (obj) {
        if (offOff === true) {
            offOff = false
            var confirmBox = $('<div class="alertBox"></div>')
            // var confirmHead = $('<div class="alertHead">' + obj.title + '</div>')
            var confirmMes = $('<div class="alertMes">' + obj.message + '</div>')
            var confirmBtn = $('<span class="ConBtn">' + obj.confirm + '</span>').on('click', function () {
                qback.remove();
                confirmBox.remove();
                offOff= true;
                if(obj.hasOwnProperty('define')){
                    setTimeout(function () {
                        obj.define()
                    }, 100)
                }
            })
            var confirmcancel = $('<span class="cancel">取消</span>').on('click', function () {
                qback.remove();
                confirmBox.remove();
                offOff= true;
                if(obj.hasOwnProperty('define')){
                    setTimeout(function () {
                        obj.cancel();
                    }, 100)
                }
            })
            // confirmBox.append(confirmHead);
            confirmBox.append(confirmMes);
            confirmBox.append(confirmBtn);
            confirmBox.append(confirmcancel);
            qback.append(confirmBox);
            $('body').append(qback);
            confirmBox.css({
                'marginTop': -confirmBox.outerHeight() / 2 + 'px'
            });
        }
       
    }, _this.Etoast = function (mes) {
        if (offOff === true) {
            offOff = false
            var timer = null;
            var ToastBox = $('<div class="ToastBox">' + mes + '</div>')
            qback.append(ToastBox);
            $('body').append(qback);
            ToastBox.css({
                'marginTop': -ToastBox.outerHeight() / 2 + 'px'
            });
            clearInterval(timer);
            timer = setInterval(function () {
                clearInterval(timer);
                qback.remove();
                ToastBox.remove();
                offOff= true;
            }, 1500);
        }
    }
}